
/*
 * 2020년을 기준으로 자신의 나이를 출력하시오.
 */
public class VarTest1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int currenYear = 2020;
		int b = 1995;
		int a = 0;
		
		a = currenYear - b + 1;
		System.out.println("저의 나이는 " + a + "입니다.");
		System.out.println(35 + 1 + "과열");
		
//		숫자 + 숫자 = 숫자
//		숫자 + 글자 = 글자
//		글자 + 글자 = 글자
//		글자 + 숫자 = 글자
		
	}

}